create table gl_clobs
( id number
, unicodelobval clob character set unicode
, latinlobval clob character set latin
);

insert into gl_clobs
select  id
,       case when id = 2 then
            empty_clob()
        else
            rpad(substr('',
                        random(1,1919),256)
                ,1024*power(2,id+4),'_')
        end
,       case when id = 4 then
            empty_clob()
        else
            rpad(substr('',
                        random(1,1919),256)
                ,1024*power(2,id+4),'_')
        end
from    generated_ids
where id <= 5;
